// تحديد أنواع الفئات المتاحة للزي الموحد
export type UniformCategory = 
  | 'aviation_uniforms'
  | 'cleaning_uniforms'
  | 'construction_uniforms'
  | 'corporate_uniforms'
  | 'healthcare_uniforms'
  | 'hospitality_uniforms'
  | 'security_uniforms';

// الحصول على مسار الصورة المناسب للفئة
export function getCategoryImagePath(category: UniformCategory): string {
  return `/images/${category}`;
}

// الحصول على مسار صورة الكاتب
export function getAuthorImagePath(imageName: string): string {
  return `/images/author/${imageName}`;
}

// تحويل سلاق URL إلى اسم فئة
export function slugToCategoryName(slug: string): string {
  return slug.replace(/-/g, '_') as UniformCategory;
}

// تحويل اسم فئة إلى سلاق URL
export function categoryNameToSlug(category: UniformCategory): string {
  return category.replace(/_/g, '-');
}

// تحويل اسم فئة إلى اسم عربي مناسب
export function categoryToArabicName(category: UniformCategory): string {
  const categoryMap: Record<UniformCategory, string> = {
    'aviation_uniforms': 'زي الطيران',
    'cleaning_uniforms': 'زي النظافة',
    'construction_uniforms': 'زي الإنشاءات',
    'corporate_uniforms': 'الزي الرسمي للشركات',
    'healthcare_uniforms': 'زي القطاع الصحي',
    'hospitality_uniforms': 'زي الضيافة والفنادق',
    'security_uniforms': 'زي الأمن والحراسة',
  };
  
  return categoryMap[category] || 'زي موحد';
}

// إنشاء تأثير blur من اللون الأساسي للفئة
export function getCategoryBlurColor(category: UniformCategory): string {
  const colorMap: Record<UniformCategory, string> = {
    'aviation_uniforms': 'data:image/jpeg;base64,',
    'cleaning_uniforms': 'data:image/jpeg;base64,',
    'construction_uniforms': 'data:image/jpeg;base64,',
    'corporate_uniforms': 'data:image/jpeg;base64,',
    'healthcare_uniforms': 'data:image/jpeg;base64,',
    'hospitality_uniforms': 'data:image/jpeg;base64,',
    'security_uniforms': 'data:image/jpeg;base64,',
  };
  
  return colorMap[category] || '';
}

// إنشاء قائمة بالفئات المتاحة
export function getAvailableCategories(): UniformCategory[] {
  return [
    'aviation_uniforms',
    'cleaning_uniforms',
    'construction_uniforms',
    'corporate_uniforms',
    'healthcare_uniforms',
    'hospitality_uniforms',
    'security_uniforms'
  ];
}

// الحصول على مسار الصورة البديلة للفئة
export function getCategoryPlaceholderPath(category: UniformCategory): string {
  return `/images/${category}/placeholder.jpg`;
}

// التحقق من صحة مسار الصورة
export function validateImagePath(path: string): boolean {
  // التحقق من أن المسار يتبع النمط المطلوب: /images/category_name/image.jpg
  return !!path.match(/^\/images\/[^\/]+\/[^\/]+\.[a-zA-Z]+$/);
}

// إصلاح مسار الصورة ليتوافق مع النمط المطلوب
export function fixImagePath(path: string, defaultCategory: string = 'uniform'): string {
  if (!path) return `/images/placeholder.jpg`;
  
  // إذا كان المسار صحيحاً، أعده كما هو
  if (validateImagePath(path)) return path;
  
  // إذا كان المسار يبدأ بـ /images/ لكنه لا يتبع النمط المطلوب
  if (path.startsWith('/images/')) {
    const parts = path.split('/');
    if (parts.length >= 3) {
      // تأكد من وجود اسم الفئة واسم الصورة
      return `/images/${parts[2]}/${parts[parts.length - 1]}`;
    }
  }
  
  // إذا كان المسار لا يبدأ بـ / أو لا يحتوي على http، أضفه إلى المسار الافتراضي
  if (!path.startsWith('/') && !path.includes('http')) {
    return `/images/${defaultCategory}/${path}`;
  }
  
  return path;
} 